mapper.select:
select

from dt_ac_link


    and user_serial = #{userSerial,jdbcType=BIGINT}


    and xh = #{xh,jdbcType=INTEGER}



    and card_serial like #{cardSerialLike}



    and card_order = #{cardOrder,jdbcType=INTEGER}


    and ac_money = #{acMoney,jdbcType=DECIMAL}


    and ac_addm = #{acAddm,jdbcType=DECIMAL}


    and ac_addo = #{acAddo,jdbcType=DECIMAL}


    and ac_subm = #{acSubm,jdbcType=DECIMAL}


    and ac_subo = #{acSubo,jdbcType=DECIMAL}


    and ac_regm = #{acRegm,jdbcType=DECIMAL}


    and ac_make = #{acMake,jdbcType=DECIMAL}


    and ac_eachm = #{acEachm,jdbcType=INTEGER}


    and ac_eacho = #{acEacho,jdbcType=INTEGER}


    and ac_blockm = #{acBlockm,jdbcType=DECIMAL}


    and ac_blocks = #{acBlocks,jdbcType=DECIMAL}


    and ac_blocke = #{acBlocke,jdbcType=INTEGER}


    and sub_kssj = #{subKssj,jdbcType=TIMESTAMP}


    and sub_jssj = #{subJssj,jdbcType=TIMESTAMP}


    and sub_order = #{subOrder,jdbcType=INTEGER}


    and jl_count = #{jlCount,jdbcType=INTEGER}


    and ac_state = #{acState,jdbcType=INTEGER}


    and sj = #{sj,jdbcType=TIMESTAMP}



    and gly_no like #{glyNoLike}



    and each_jssj = #{eachJssj,jdbcType=TIMESTAMP}


    and each_kssj = #{eachKssj,jdbcType=TIMESTAMP}


    and each_order = #{eachOrder,jdbcType=INTEGER}



    and card_hao like #{cardHaoLike}




    and (user_serial = #{keys} or
    xh = #{keys} or
    card_serial like #{keysLike} or
    card_order = #{keys} or
    ac_money = #{keys} or
    ac_addm = #{keys} or
    ac_addo = #{keys} or
    ac_subm = #{keys} or
    ac_subo = #{keys} or
    ac_regm = #{keys} or
    ac_make = #{keys} or
    ac_eachm = #{keys} or
    ac_eacho = #{keys} or
    ac_blockm = #{keys} or
    ac_blocks = #{keys} or
    ac_blocke = #{keys} or
    sub_kssj = #{keys} or
    sub_jssj = #{keys} or
    sub_order = #{keys} or
    jl_count = #{keys} or
    ac_state = #{keys} or
    sj = #{keys} or
    gly_no like #{keysLike} or
    each_jssj = #{keys} or
    each_kssj = #{keys} or
    each_order = #{keys} or
    card_hao like #{keysLike})
